const Login = () => {

  const[email,setEmail]=useState('');
  const[password,setPassword]=useState('');
  const history=useHistory()

  const loginuser=async(e)=>{
    e.preventDefault();
    const res= await fetch('/login',{
      method:"POST",
      headers:{
        "Content-type":"application/JSON"
      },
      body:JSON.stringify({
        email,password
      })
    })
    const data=res.json()
    if (!data) {
      window.alert("invalid credentials")
    }
    else{
      history.push("/")
    }
  }
  return (
    
    <Container>
        <Wrapper>
            <Title>SIGN IN</Title>
            <br/> 
            <Link to ='/' style ={{textDecoration:'none' ,color:'Maroon',cursor:'pointer'}}>
                <Logo>
                                    attHire.
                </Logo>
                </Link>
<br/>
            <Form method="POST">
              <h4>Email</h4>
                <Input 
                placeholder = "Username or Email" 
                value={email}
                onChange={(e)=>setEmail(e.target.value)}
                
                ></Input>
                <br/>
                <h4>Password</h4>
                <Input 
                placeholder = "Password"
                value={password}
                onChange={(e)=>setPassword(e.target.value)}
                
                ></Input>
               <p> <Button onClick={loginuser}>LOG IN</Button>
                <Link to="/forgotPassword">
                <h4 style={{ marginBottom: "5%", fontWeight: "bold",color:'black',cursor:'pointer' }}>
                  Forgot password?
                </h4>
                
              </Link>
              </p>
                {/* <Link style = {{cursor:'pointer'}}>Forgotten your Password?</Link> */}
              <p> Not a Member? <Link to ='/Register' style = {{color:'black',cursor:'pointer'}}>Join Us.
     </Link> </p>
     
            </Form>
            
        </Wrapper>
    </Container>
   
  )
}

export default Login
