import Footer from '../components/Footer'
import styled from 'styled-components'
import Announcement from '../components/Announcement'
import Navbar from '../components/Navbar'
import Newsletter from '../components/Newsletter'
import BottomFooter from '../components/BottomFooter'
import React from 'react'
import { mobile } from '../responsive'
import { Link } from 'react-router-dom'


const [popup, setPop] = useState(false)
  const handleClickOpen = () => {
    setPop(!popup)
  }
  const closePopup = () => {
    setPop(false)
  }
const Container = styled.div`

`

const Wrapper = styled.div`
    padding:20px;
    ${mobile({ padding: "10px" })}
`
const Title = styled.div`
font-size: 50px;
margin: 1px 1px;
padding : 15px
font-weight:600;
text-align:center;
`
const Rent = styled.div`
font-weight:200;
font-size: 30px;
text-align:center;

`;
const Button = styled.button`
    width:15%;
    border:none;
    padding:15px 20px;
    background-color:black;
    color:white;
    cursor:pointer;
    margin-bottom:10px;
    margin-right:20px;
   
`
const Are = styled.div`
font-weight:500;
font-size: 35px;
text-align:center;
`;

const Seller = () => {
    //   const user = useSelector((state) => state.user.currentUser);

    return (
        <Container>
            <Navbar />
            <Announcement />
            <Wrapper>
                <Title >"Dress to Express"</Title><br /><br />
                <Are> Are you worried of buying new dress for one Occasion?</Are><br /><br />
                <Rent> Here! you can get a dress as per your wish and rent it for some days and return after it's use only on 10% rent per day of cost price.<br /> <br />You can Rent or Sell  your unused cloths to other people and earn money. <br /><br /><br />
                    <div>
                        {
                            popup ?
                                <div style={styles.main} className="main">
                                    <div style={styles.popup} className="popup">
                                        <div className="header">
                                            <h2 style={{ cursor: "pointer" }} onClick={closePopup}>X</h2>
                                            <Link to='/' style={{ textDecoration: 'none', color: 'Maroon', cursor: 'pointer' }}>
                                                <Logo>
                                                    attHire.
                                                </Logo>
                                            </Link><br />
                                            <Text>For how many days you want to rent this cloth? </Text>
                                            <br /><br /><br />
                                            <Button style={{
                                                width: "40%",
                                                border: "none",
                                                padding: "15px 20px",
                                                backgroundColor: "black",
                                                color: "white",
                                                cursor: "pointer",
                                                marginLeft: "150px"
                                            }} onClick={handleClick} >ADD TO CART</Button>

                                        </div>

                                    </div>
                                </div> : ""
                        }
                    </div>
                    <Link to='/sell' style={{ color: 'black', cursor: 'pointer' }}><Button>SELL</Button> </Link>
                    <Link to='/Hire' style={{ color: 'black', cursor: 'pointer' }}><Button>HIRE</Button> </Link>
                </Rent>
            </Wrapper>
            <Newsletter />
            <Footer />
            <BottomFooter />
        </Container>
    )
}

const styles = {
    main: {
      backgroundColor: "rgba(39, 145, 216,0.710)",
      height: "100vh",
      width: "100%",
      position: "absolute",
      top: "0",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      left: "0",
      bottom: "0",
  
    },
    popup: {
      height: "400px",
      width: "50%",
      backgroundColor: "white",
      position: "absolute",
      top: "25%",
      textAlign: 'right',
  
      // display:"flex",
      // justifyContent:"center",
      // alignItems:"center",
      right: "25%",
    },
  
    header: {
      display: "flex",
      justifyContent: "space-between",
      padding: "0 30px 0 15px",
      borderBottom: "2px solid black",
    }
  };
  
export default Seller
